document.addEventListener('DOMContentLoaded', () => {
  let toggle = document.getElementById("user_menu");
  let dropmenu = document.getElementById("login_dropmenu");

  toggle?.addEventListener('click',function(){
    dropmenu.classList.toggle("active");
  });
});

document.addEventListener("turbo:load", () => {
  let dotbutton = document.getElementById("dotsicon_text");
  let facilitymenu = document.getElementById("dotsicon_dropmenu");

  dotbutton.addEventListener('click',function(){
    facilitymenu.classList.toggle("active");
  });
});

document.addEventListener('DOMContentLoaded', () => {
  const viewPriceDiv = document.querySelector('.view_price');
  const checkinInput = document.getElementById('checkin_day');
  const checkoutInput = document.getElementById('checkout_day');
  const guestInput = document.getElementById('guest_count');
  const nightSpan = document.getElementById('night_count');
  const totalSpan = document.getElementById('total_price');

  const roomRate = parseFloat(viewPriceDiv.dataset.roomRate);

  function calculate() {
    const checkin = new Date(checkinInput.value);
    const checkout = new Date(checkoutInput.value);
    const guestCount = parseInt(guestInput.value, 10);

    if (checkin && checkout && checkout > checkin && guestCount > 0) {
      const nights = (checkout - checkin) / (1000 * 60 * 60 * 24);
      const total = nights * roomRate * guestCount;

      nightSpan.textContent = nights;
      totalSpan.textContent = total.toLocaleString(); 
    } else {
      nightSpan.textContent = 0;
      totalSpan.textContent = 0;
    }
  }

  checkinInput.addEventListener('change', calculate);
  checkoutInput.addEventListener('change', calculate);
  guestInput.addEventListener('input', calculate); 
});
